mapper.select:
SELECT a.BOARD_NUM, a.BOARD_TITLE, a.BOARD_CONT, a.BOARD_WRITER, a.BOARD_DATE, a.BOARD_AF, a.BOARD_COUNT, a.BOARD_BC FROM ( SELECT ROWNUM rnum, s.* FROM ( SELECT BOARD_NUM, BOARD_TITLE, BOARD_CONT, BOARD_WRITER, BOARD_DATE, BOARD_AF, BOARD_COUNT, BOARD_BC FROM BOARD WHERE BOARD_BC ='FAQ'
AND ${searchFiled} LIKE '%' || #{searchValue} || '%'
ORDER BY BOARD_NUM DESC )s )a WHERE rnum BETWEEN #{startRowNo} AND #{endRowNo}

mapper.insert:
		INSERT INTO BOARD(
		BOARD_NUM
		,BOARD_TITLE
		,BOARD_CONT
		,BOARD_WRITER
		,BOARD_DATE
		
		,BOARD_AF
		
		,BOARD_BC
		,BOARD_COUNT
		)
		VALUES(
		#{BOARD_NUM, jdbcType=VARCHAR}
		,#{BOARD_TITLE, jdbcType=VARCHAR}
		,#{BOARD_CONT, jdbcType=VARCHAR}
		,#{BOARD_WRITER, jdbcType=VARCHAR}
		,SYSDATE
		
		,#{BOARD_AF, jdbcType=VARCHAR}
		
		,#{BOARD_BC, jdbcType=VARCHAR}
		,0
		)

mapper.update:
	update board set BOARD_TITLE=#{BOARD_TITLE, jdbcType=VARCHAR}
					,BOARD_CONT=#{BOARD_CONT, jdbcType=VARCHAR}
					,BOARD_DATE=sysdate
					,BOARD_AF=#{BOARD_AF, jdbcType=VARCHAR}
               where BOARD_NUM=#{BOARD_NUM, jdbcType=VARCHAR}

mapper.select:
select * from board where BOARD_BC=#{BOARD_BC}
and ${searchFiled} like '%' || #{searchValue} || '%'
ORDER BY BOARD_NUM DESC

mapper.update:
update board
set BOARD_COUNT = #{BOARD_COUNT} + 1
where BOARD_NUM = #{BOARD_NUM}
