mapper.select:
SELECT scrappl_yr,scrappl_qtr,scr_nm,scr_money,stud_bank,stud_ah,stud_act_num 
 FROM SCR,SCRAPPL,STUDENT
 WHERE student_num=#{scrappl_stud}
 AND student_num=scrappl_stud
 AND scrappl_scr=scr_num
 
 AND scrappl_yr=#{scrappl_yr}
 
 
 AND scrappl_qtr=#{scrappl_qtr}

SELECT  scrappl_yr,scrappl_qtr,scr_nm,scrappl_cause,stud_bank,stud_ah,stud_act_num,scrappl_date 
 FROM SCR,SCRAPPL,STUDENT
  WHERE student_num=#{scrappl_stud}
 AND student_num=scrappl_stud
 AND scrappl_scr=scr_num
 
 AND scrappl_yr=#{scrappl_yr}
 
 
 AND scrappl_qtr=#{scrappl_qtr}
