mapper.select:
    SELECT 
         a.stud_num, a.stud_nm,a.stud_email,a.stud_grd,a.stud_gen,a.stud_schreg_code,a.stud_hp,a.stud_dept
    FROM (
        SELECT ROWNUM rnum, s.*
        FROM (
            	SELECT stud_num, stud_nm,stud_email,stud_grd,stud_gen,stud_schreg_code,stud_hp,stud_dept
            	FROM STUDENT, PROFESSOR
            	WHERE PRFS_DEPT = STUD_DEPT
              AND PRFS_NUM = #{prfs_num}
            
                AND ${searchFiled} LIKE '%' || #{searchValue} || '%'
            
        )s
    )a 
WHERE rnum BETWEEN #{startRowNo} AND #{endRowNo}
